mapper.insert:
insert into user (id, password, name, address, email, phone, allergy)
values ( #{id:VARCHAR}
        ,#{password:VARCHAR}
        ,#{name:VARCHAR}
        ,#{address:VARCHAR}
        ,#{email:VARCHAR}
        ,#{phone:VARCHAR}
        ,#{allergy:VARCHAR}
        )

mapper.update:
   		update user set password = #{password:VARCHAR}
		   		        , name = #{name:VARCHAR}
		   		        , address = #{address:VARCHAR}
		   		        , email = #{email:VARCHAR}
		   		        , phone = #{phone:VARCHAR}
		   		        , allergy = #{allergy:VARCHAR}
   		where  id = #{id:VARCHAR}
